package com.wufuqiang.leetcode;

import java.util.HashMap;
import java.util.Map;

public class L76_MinWindow {
	public static void main(String[] args) {
		String str = "";
		System.out.println(str.length());
	}
	public String minWindow(String s , String t){

		Map<Character,Integer> needs = new HashMap<>();
		Map<Character,Integer> window = new HashMap<>();
		for(char c : t.toCharArray()){
			Integer i = needs.getOrDefault(c, 0);
			i+=1;
			needs.put(c,i);
		}

		int low = 0;
		int high = 0;
		int match = 0;
		int minLen = Integer.MAX_VALUE;
		int start = 0;
		boolean flag = false;
		while(high<s.length()){
			char tmp = s.charAt(high);
			if(needs.containsKey(tmp)){
				Integer i = window.getOrDefault(tmp, 0);
				i+=1;
				window.put(tmp,i);
				if(needs.get(tmp).equals(i)){
					match+=1;
				}
			}
			high+=1;
			while(match == needs.size()){
				flag= true;
				if(high-low < minLen){
					start = low;
					minLen=high-low;
				}
				char tmp2 = s.charAt(low);
				if(needs.containsKey(tmp2)){
					Integer i = window.get(tmp2);
					i-=1;
					window.put(tmp2,i);
					if(i<needs.get(tmp2)){
						match-=1;
					}
				}
				low+=1;
			}

		}

		return flag?s.substring(start,start+minLen):"";
	}

}
